const EntityManager = {
  Create: (name) => {
    Game.Entity[name] =
    `Create() {}
     Update() {}
     Render() {}
     Delete() {} //Flush() {}
    `
  },
  Edit: (name, code) => {
    Game.Entity[name] = code
  }
}

const Compiler = {
  Compile: () => {
    let src = "//This code is automatically generated by WebSource Game Engine"
    src += "\n" + PreGameFragment
    src += "\n" + InputFragment

    src += "\n" + SpriteFunctions
    //load sound functions here
    src += "\n" + EntityFunctions

    src += "\n" + Compiler.SpriteAssembler()
    //load soubds here
    //load components here
    src += "\n" + Compiler.EntityAssembler()
    //load maps here

    src += "\n" + GameFragment
    return src
  },
  SpriteAssembler: placeholderSpriteAssemler,
  EntityAssembler: placeholderEntityAssemler,
}

const PreGameFragment = `
const canvas = document.getElementById("canvas")
const ctx = canvas.getContext("2d")

canvas.width = 320
canvas.height = 240

canvas.style.width = "100%"
canvas.style.height = "100%"
`

const SpriteFunctions = `
//Sprite functions
function loadSprite(path) {
  let spr = new Image();
  spr.src = path;
  return spr;
};
`

const EntityFunctions = `
//EntityFunctions functions

`

const GameFragment = `
let mainArray = [] //set it to equal to the initial level

mainArray.push(new Player())

requestAnimationFrame(gameLoop) //<- make it that, this will only run when assets are loaded

function gameLoop() {
  ctx.clearRect(0, 0, canvas.width, canvas.height)
  mainArray.forEach(ent=> ent?.Update())
  mainArray.forEach(ent=> ent?.Render())
  requestAnimationFrame(gameLoop)
};
`

const InputFragment = `
let Input = {
  w: false,
  s: false,
  a: false,
  d: false
};

window.addEventListener("keydown", (event)=> {
  Input[event.key] = 1;
});

window.addEventListener("keyup", (event)=> {
  Input[event.key] = 0;
});
`

function placeholderSpriteAssemler() {
  let arr = {...Game.Sprite}
  let temp = `//Sprites \n`

  for(const key in arr) {
    temp += `const ${key} = loadSprite("${arr[key]}")` + "\n"
  }

  return temp
}

function placeholderEntityAssemler() {
  let arr = {...Game.Entity}
  let temp = `//Entitites`

for(const key in arr) {
  temp +=`
  class ${key} {
    constructor() {
      this?.Create()
    }
    ${arr[key]}
  }  
  `
  }

  return temp
}

let sprFishIdleHolder = "data:image/png;base64,"
let sprFishWalkHolder = "data:image/png;base64,"

let playerplace = `
  Create() {
    this.x = 20
    this.y = 20
    this.hspeed = 0
    this.vspeed = 0
    this.health = 10
    this.speed = 1

    this.sprite = sprFishWalk
    this.prevSprite = this.sprite
    this.frame = 0
  }

  Update() {
    switch(this.sprite) {
      case sprFishWalk: {
        this.frame += 0.23
        if (this.frame > 6) this.frame = 0
        break
      }
      case sprFishIdle: {
        this.frame += 0.30
        if (this.frame > 4) this.frame = 0
        break
      }
    }

    this.hspeed = (Input.d - Input.a) * this.speed
    this.vspeed = (Input.s - Input.w) * this.speed

    this.x += this.hspeed
    this.y += this.vspeed

    this.sprite = (Input.a || Input.d || Input.w || Input.s) ? sprFishWalk : sprFishIdle
    if (this.sprite !== this.prevSprite) {this.frame = 0; this.prevSprite = this.sprite}

  }
  Render() {
    ctx.drawImage(this.sprite, 24 * Math.floor(this.frame), 0, 24, 24, Math.round(this.x), Math.round(this.y), 24, 24)
  }
`

Game.Sprite["sprFishIdle"] = sprFishIdleHolder
Game.Sprite.sprFishWalk = sprFishWalkHolder

Game.Entity.Player = playerplace


